package com.oracle.refapp.patient.helpers;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

import com.nimbusds.jose.JOSEException;
import com.nimbusds.jose.jwk.KeyType;
import com.nimbusds.jwt.SignedJWT;
import io.micronaut.security.token.jwt.signature.jwks.DefaultJwkValidator;
import io.micronaut.security.token.jwt.signature.jwks.JwkValidator;
import java.text.ParseException;
import java.util.List;
import java.util.Optional;
import org.junit.jupiter.api.Test;

public class CustomJwksSignatureTest {

  private final CustomJwksSignature customJwksSignature;

  private static final String KEYS =
    "{\"keys\":[{\"kty\":\"RSA\",\"x5t#S256\":\"12FSWG2pGnxIx-w_3NuMIEoRfgirf7eTd2evsdRCMuc\",\"e\":\"AQAB\",\"x5t\":\"mf6WcCcYZxPjk5Kme7C3Z3YSZ2M\",\"kid\":\"SIGNING_KEY\",\"x5c\":[\"\",\"\"],\"key_ops\":[\"wrapKey\",\"verify\",\"encrypt\"],\"alg\":\"RS256\",\"n\":\"m97v6-BnCEDT9Crr9DRPznfM4v-ggpnHeP__edZaWWIm72kitPdszjy_uZGRIxh6NLnHVVloIx_jxdg-q_qxNec2EZNJ2eAKVmw0eGgelz2mUq8rqgWwMZjNkBOTMaWb23QzL3LhiwrOVVfrCdu4MtBXbURbyh2JC4u9cj-gonfW3WDWtYUC-ZUQxOR_O2X_GZyyhMC66dtIgvcwKufO4wA-7D1Bf3yJJbJfWWHxjzY0jknFc5OR2p7xlXliaLkdFh2xEOr_mKwcUkjg1fwnkHnKjVcLoAyQi0YXnL7C7f4_KrQpYFcRe2jfZBsILqXnVcTUHFXVAjlWRAb3mgRNlQ\"}]}";

  private static final String JWT =
    "eyJ4NXQjUzI1NiI6IjEyRlNXRzJwR254SXgtd18zTnVNSUVvUmZnaXJmN2VUZDJldnNkUkNNdWMiLCJ4NXQiOiJtZjZXY0NjWVp4UGprNUttZTdDM1ozWVNaMk0iLCJraWQiOiJTSUdOSU5HX0tFWSIsImFsZyI6IlJTMjU2In0..kocv3Fr1NS93DiyiN2OclIAp_oX_wp-FjLOz_mT6ZUsTnonMV2O9n3st0sfcm0vh1wY1a4OmCwNnsFX7kOd7JH7CumtkroFH0pjT_Gp-yyb9mjnSUQ98_IrWKviG5ztpGTng96fK2IPp1Qr3ZeHlKCJToToCT6aObSiVZ3r-MNByUQbQHAmWn3JdG4xGSYplDTKVtARucrGuSqpsOfVDuLRvPCgwNcGPXFJrt6UddNNGD8pzvlgiE-fUCxC_vdVfv9UPiOwcT1nk7EquU314i3fX3QadYaYIFuFmdJBdkxQ7YZqv0yHO2w3OgIlNHJBD3Xe54aYlXqZDbaVuGDG_Rw";

  public CustomJwksSignatureTest() throws ParseException {
    CustomJwksSignatureConfiguration customJwksSignatureConfiguration = new CustomJwksSignatureConfiguration(
      KeyType.RSA,
      KEYS
    );
    JwkValidator jwkValidator = new DefaultJwkValidator();
    this.customJwksSignature = new CustomJwksSignature(customJwksSignatureConfiguration, jwkValidator);
  }

  @Test
  public void testVerify() throws ParseException, JOSEException {
    assertTrue(this.customJwksSignature.verify(SignedJWT.parse(JWT)));
  }

  @Test
  public void testGetKeys() {
    Optional<List<String>> optionalKeyIds = this.customJwksSignature.getKeyIds();
    assertFalse(optionalKeyIds.isEmpty());
  }
}
